const changeSortingHandler=()=>{
history.push(`/quotes?sort=asc`)
}

  return (
    <Fragment>
      <div className={classes.sorting}>
<button onClick={changeSortingHandler}>Sort Ascending</button>
      </div>
      <ul className={classes.list}>
        {props.quotes.map((quote) => (
          <QuoteItem
            key={quote.id}
            id={quote.id}
            author={quote.author}
            text={quote.text}
          />
        ))}
      </ul>
    </Fragment>
  );
};

